import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import FeatureCard from '../components/FeatureCard';
import Footer from '../components/Footer';

const LandingPage = () => {
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState(false);
  const [scrollY, setScrollY] = useState(0);
  
  // Animation on scroll effect
  useEffect(() => {
    setIsVisible(true);
    
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);
  
  return (
    <div className="min-h-screen flex flex-col">
      {/* Hero Section with Gradient Background */}
      <section className="bg-gradient-to-br from-indigo-50 via-blue-50 to-white py-20 px-4 relative overflow-hidden">
        <div className="absolute top-0 left-0 w-full h-full">
          <div className="absolute top-20 left-10 w-64 h-64 rounded-full bg-indigo-200 opacity-20 animate-pulse-slow"></div>
          <div className="absolute bottom-10 right-10 w-80 h-80 rounded-full bg-blue-200 opacity-20 animate-pulse-slow"></div>
        </div>
        
        <div className="container mx-auto text-center relative z-10">
          <div className={`transform transition-all duration-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
            <h1 className="text-5xl md:text-6xl font-bold mb-6 text-transparent bg-clip-text bg-gradient-to-r from-indigo-700 to-blue-500">
              Insurance Product Recommendation GenAI Agent
            </h1>
            <p className="text-xl md:text-2xl text-gray-600 mb-10 max-w-3xl mx-auto">
              Your AI-powered insurance advisor for personalized financial planning
            </p>
            
            <button 
              onClick={() => navigate('/recommendation')}
              className="px-8 py-4 bg-gradient-to-r from-indigo-600 to-blue-500 text-white font-bold rounded-lg shadow-lg hover:shadow-xl transform transition-all duration-300 hover:-translate-y-1 hover:scale-105"
            >
              Get Started
              <svg className="w-5 h-5 ml-2 inline-block" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14 5l7 7m0 0l-7 7m7-7H3"></path>
              </svg>
            </button>
          </div>
        </div>
      </section>
      
      {/* Main Content */}
      <div className="container mx-auto px-4 flex-grow py-12">
        {/* Problem Statement */}
        <section className={`bg-white p-8 rounded-xl shadow-lg mb-16 border-l-4 border-indigo-600 transform transition-all duration-700 ${scrollY > 100 ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          <h2 className="text-3xl font-semibold mb-6 text-indigo-700">🔍 Problem Statement</h2>
          <p className="text-gray-600 text-lg leading-relaxed">
            Navigating the complex world of insurance products can be overwhelming. Most people struggle to 
            understand which insurance plans best suit their unique financial situation, goals, and risk profile. 
            Our GenAI Agent bridges this gap by providing tailored recommendations based on your personal profile.
          </p>
        </section>
        
        {/* Features Section */}
        <section className="my-16">
          <h2 className="text-3xl font-semibold mb-10 text-center text-indigo-700">✨ Key Features</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <FeatureCard 
              icon="🎯" 
              title="Personalized Recommendations" 
              description="Get insurance product suggestions tailored to your specific financial profile and goals"
              delay={0.1}
            />
            
            <FeatureCard 
              icon="📋" 
              title="Tax Benefit Analysis" 
              description="Understand the tax advantages associated with recommended insurance products"
              delay={0.3}
            />
            
            <FeatureCard 
              icon="💰" 
              title="Premium Estimates" 
              description="Receive instant estimates of potential premium costs based on your income"
              delay={0.5}
            />
            
            <FeatureCard 
              icon="🤖" 
              title="AI-Powered Insights" 
              description="Get human-like explanations that break down complex insurance concepts"
              delay={0.7}
            />
          </div>
        </section>
        
        {/* How It Works Section */}
        <section className="my-16">
          <h2 className="text-3xl font-semibold mb-10 text-center text-indigo-700">🔄 How It Works</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {[
              {
                step: 1,
                title: "Enter Your Details",
                description: "Provide your age, income, financial goals, and risk appetite",
                icon: "📝"
              },
              {
                step: 2,
                title: "AI Analysis",
                description: "Our GenAI agent analyzes your profile and matches it with suitable products",
                icon: "⚙️"
              },
              {
                step: 3,
                title: "Get Recommendations",
                description: "Receive personalized insurance product recommendations with detailed explanations",
                icon: "✅"
              }
            ].map((item, index) => (
              <div 
                key={index} 
                className={`bg-white p-6 rounded-xl shadow-md border-t-4 border-indigo-600 transform transition-all duration-700 hover:shadow-lg hover:-translate-y-1 ${scrollY > 300 ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
                style={{ transitionDelay: `${index * 200}ms` }}
              >
                <div className="w-16 h-16 bg-indigo-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-3xl">{item.icon}</span>
                </div>
                <div className="absolute -top-4 -left-4 w-8 h-8 bg-indigo-600 rounded-full flex items-center justify-center text-white font-bold">
                  {item.step}
                </div>
                <h3 className="text-xl font-semibold mb-2 text-center text-indigo-700">{item.title}</h3>
                <p className="text-gray-600 text-center">{item.description}</p>
              </div>
            ))}
          </div>
        </section>
        
        {/* CTA Section */}
        <section className="bg-gradient-to-r from-indigo-50 to-blue-50 p-12 rounded-xl text-center my-16 shadow-lg transform transition-all duration-700">
          <h2 className="text-3xl font-semibold mb-4 text-indigo-700">Ready to find your perfect insurance match?</h2>
          <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">Get personalized recommendations in just a few clicks</p>
          <button 
            className="px-8 py-4 bg-gradient-to-r from-indigo-600 to-blue-500 text-white font-bold rounded-lg shadow-lg hover:shadow-xl transform transition-all duration-300 hover:-translate-y-1 hover:scale-105"
            onClick={() => navigate('/recommendation')}
          >
            Start Now
            <svg className="w-5 h-5 ml-2 inline-block" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14 5l7 7m0 0l-7 7m7-7H3"></path>
            </svg>
          </button>
        </section>
      </div>
      
      <Footer />
    </div>
  );
};

export default LandingPage;
