import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';
import ProductExpander from '../components/ProductExpander';
import { loadInsuranceProducts } from '../utils/csvDataLoader';
import { recommendProducts, generateResponse, generateAIRecommendation } from '../utils/recommendationUtils';

const RecommendationPage = () => {
  const navigate = useNavigate();
  const [age, setAge] = useState(30);
  const [income, setIncome] = useState(500000);
  const [financialGoal, setFinancialGoal] = useState('Tax Saving');
  const [riskAppetite, setRiskAppetite] = useState('Medium');
  const [recommendationResult, setRecommendationResult] = useState(null);
  const [insuranceProducts, setInsuranceProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [useAI, setUseAI] = useState(true);
  const [aiLoading, setAiLoading] = useState(false);

  // Define the new financial goals
  const financialGoals = [
    "Tax Saving",
    "Wealth Creation",
    "Life Cover",
    "Health Security",
    "Emergency Fund",
    "Retirement Planning",
    "Child Education",
    "Estate Planning"
  ];

  // Load insurance products from CSV when component mounts
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const products = await loadInsuranceProducts();
        setInsuranceProducts(products);
        setLoading(false);
      } catch (err) {
        console.error('Failed to load insurance products:', err);
        setError('Failed to load insurance products. Please try again later.');
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (insuranceProducts.length === 0) {
      setError('No insurance products available. Please try again later.');
      return;
    }

    const userProfile = {
      age,
      income,
      financialGoal,
      riskAppetite
    };

    const recommendations = recommendProducts(insuranceProducts, userProfile);

    if (useAI) {
      setAiLoading(true);
      try {
        const aiResponse = await generateAIRecommendation(userProfile, recommendations);
        setRecommendationResult({
          products: recommendations,
          response: aiResponse
        });
      } catch (error) {
        console.error('Error generating AI recommendation:', error);
        // Fall back to standard response
        const standardResponse = generateResponse(userProfile, recommendations);
        setRecommendationResult({
          products: recommendations,
          response: standardResponse
        });
      } finally {
        setAiLoading(false);
      }
    } else {
      const standardResponse = generateResponse(userProfile, recommendations);
      setRecommendationResult({
        products: recommendations,
        response: standardResponse
      });
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading insurance products...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center bg-red-50 p-6 rounded-xl shadow-md">
          <div className="text-red-500 text-5xl mb-4">⚠️</div>
          <h2 className="text-xl font-semibold mb-2 text-red-700">Error</h2>
          <p className="text-gray-700 mb-4">{error}</p>
          <button 
            onClick={() => navigate('/')}
            className="btn-primary"
          >
            Back to Home
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col">
      {/* Hero Section */}
      <section className="hero-section" style={{ paddingTop: '2rem', paddingBottom: '2rem' }}>
        <div className="container mx-auto text-center">
          <h1 className="text-4xl font-bold mb-4">Insurance Product Recommendation</h1>
          <p className="text-xl text-gray-600">Fill in your details below to get personalized insurance recommendations</p>
        </div>
      </section>

      {/* Main Content */}
      <div className="container mx-auto px-4 flex-grow">
        {/* Form Container */}
        <div className="form-container">
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <h2 className="text-xl font-semibold mb-4">Your Profile</h2>

                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Age</label>
                  <input 
                    type="range" 
                    min="18" 
                    max="100" 
                    value={age}
                    onChange={(e) => setAge(parseInt(e.target.value))}
                    className="w-full"
                  />
                  <div className="flex justify-between text-sm text-gray-600">
                    <span>18</span>
                    <span>Current: {age}</span>
                    <span>100</span>
                  </div>
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Annual Income (₹)</label>
                  <input 
                    type="range" 
                    min="100000" 
                    max="5000000" 
                    step="50000"
                    value={income}
                    onChange={(e) => setIncome(parseInt(e.target.value))}
                    className="w-full"
                  />
                  <div className="flex justify-between text-sm text-gray-600">
                    <span>₹1L</span>
                    <span>Current: ₹{(income / 100000).toFixed(1)}L</span>
                    <span>₹50L</span>
                  </div>
                </div>
              </div>

              <div>
                <h2 className="text-xl font-semibold mb-4">Your Preferences</h2>

                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Primary Financial Goal</label>
                  <select 
                    value={financialGoal}
                    onChange={(e) => setFinancialGoal(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                  >
                    {financialGoals.map((goal, index) => (
                      <option key={index} value={goal}>{goal}</option>
                    ))}
                  </select>
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Risk Appetite</label>
                  <select 
                    value={riskAppetite}
                    onChange={(e) => setRiskAppetite(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                  >
                    <option value="Low">Low</option>
                    <option value="Medium">Medium</option>
                    <option value="High">High</option>
                  </select>
                </div>

                <div className="mb-4">
                  <label className="flex items-center text-gray-700">
                    <input
                      type="checkbox"
                      checked={useAI}
                      onChange={(e) => setUseAI(e.target.checked)}
                      className="mr-2 h-5 w-5"
                    />
                    Use AI-powered recommendations
                  </label>
                </div>
              </div>
            </div>

            <div className="text-center mt-6">
              <button type="submit" className="btn-primary" disabled={aiLoading}>
                {aiLoading ? 'Generating Recommendations...' : 'Get Recommendations'}
              </button>
            </div>
          </form>
        </div>

        {/* Results Section */}
        {recommendationResult && (
          <div className="result-card">
            <h2 className="text-2xl font-semibold mb-6 text-center pb-4 border-b border-gray-200">
              Your {useAI ? 'AI-Powered' : 'Personalized'} Recommendation
            </h2>
            <div className="whitespace-pre-line text-gray-700 mb-8">
              {recommendationResult.response}
            </div>

            {recommendationResult.products.length > 0 && (
              <div>
                <h3 className="text-xl font-semibold mb-4">Recommended Products</h3>
                {recommendationResult.products.map((product, index) => (
                  <ProductExpander key={index} product={product} />
                ))}
              </div>
            )}
          </div>
        )}

        {/* Back Button */}
        <div className="text-center my-8">
          <button 
            className="btn-primary"
            onClick={() => navigate('/')}
          >
            Back to Home
          </button>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default RecommendationPage;
