import React, { useState, useEffect } from 'react';

const FeatureCard = ({ icon, title, description, delay = 0 }) => {
  const [isVisible, setIsVisible] = useState(false);
  
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, delay * 1000);
    
    return () => clearTimeout(timer);
  }, [delay]);

  return (
    <div 
      className={`bg-white rounded-xl p-8 shadow-md border-l-4 border-indigo-600 transition-all duration-700 transform hover:-translate-y-2 hover:shadow-lg ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
      style={{ transitionDelay: `${delay}s` }}
    >
      <div className="text-5xl mb-6 bg-gradient-to-br from-indigo-500 to-blue-500 text-white p-4 rounded-full w-20 h-20 flex items-center justify-center mx-auto shadow-lg">
        {icon}
      </div>
      <h3 className="text-xl font-semibold mb-3 text-indigo-700 text-center">{title}</h3>
      <p className="text-gray-600 text-center">{description}</p>
    </div>
  );
};

export default FeatureCard;
